
jQuery(document).ready(function($){var toggleCode=function(button){var new_text=button.data('hide-text');var old_text=button.text();button.text(new_text);button.data('hide-text',old_text);var hide_class=button.data("hide-class");var show_class=button.data("show-class");button.addClass(hide_class)
button.removeClass(show_class);button.data("hide-class",show_class);button.data("show-class",hide_class);var hide=button.data('hide');var show=button.data('show');$(hide).hide();$(show).show();button.data('hide',show);button.data('show',hide);}
$('.toggle-code').click(function(){toggleCode($(this));});});